// src/router/index.js

import Vue from 'vue';
import VueRouter from 'vue-router';

Vue.use(VueRouter);

const routes = [
  {
    path: '/',
    name: 'Home',
    component: () => import('../views/Home.vue'), // 예시 컴포넌트 이름
  },
  {
    path: '/about',
    name: 'About',
    component: () => import('../views/About.vue'), // 예시 컴포넌트 이름
  },
  // 추가적인 라우트 정의
];

const router = new VueRouter({
  mode: 'history', // URL에서 해시(#)를 제거하는 경우
  base: process.env.BASE_URL,
  routes,
});

export default router;
