import axios from 'axios';
export default {
  data() {
    return {
      name: '',
      id: '',
      password: ''
    };
  },
  methods: {
    register() {
      axios.post('http://127.0.0.1:8000/register', {
        name: this.name,
        id: this.id,
        password: this.password
      }).then(response => {
        console.log(response.data);
      }).catch(error => {
        // 오류 처리
        console.error(error);
      });
    }
  }
};
